    //     })
    // }

    clickHandler = () => {
        console.log(this)
        this.setState({
            message: 'No, you are a tootie'
        })
    }

    render() {
        return (
            <div>
                <div>{this.state.message}</div>
                {/* <button onClick={this.clickHandler.bind(this)}>Click me</button> */}
                <button onClick={this.clickHandler}>Click me</button>

            </div>
        )
    }
}

export default EventBind
