import React, {Component} from 'react'

class Counter extends Component {

    constructor(props) {
        super(props)

        this.state = {
            count: 0,
        }
    }

    incrementCount() {
        this.setState({
            count:this.state.count + 1
        }, () => {console.log('counter: ' + this.state.count)})
    }

    incrementFive() {
        this.incrementCount()
        this.incrementCount()
        this.incrementCount()
        this.incrementCount()
        this.incrementCount()
    }

    render() {
        return (
            <div>
                <div>Count: {this.state.count}</div>
                <button onClick={() => this.incrementCount()}>Uppie</button>
            </div>
        )
    }
}

export default Counter
