import React, {Component} from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state={
            userName:''
        }
    }

    inputHandler() {
        this.setState(event => {
            userName=event.target.value
        })
    }

    render() {
        return (
            <form>
                <label>Username</label>
                <input type='text' onChange={this.inputHandler}></input>
            </form>
        )
    }
}

export default Form
