import React, {Component} from 'react'

class Counter extends Component {

    constructor(props) {
        super(props)

        this.state = {
            counter: 0,
        }
    }

    incrementCount() {
        this.setState({
            counter:this.state.counter + 1
        })
    }

    render() {
        return (
            <div>
                <div>Count: {this.state.counter}</div>
                <button onClick={() => this.incrementCount()}>Uppie</button>
            </div>
        )
    }
}

export default Counter
