import React, {Component} from 'react'
import santa from './resources/hehehe.jpg'

class Message extends Component {
    constructor() {
        super()
        this.state = {
            showSanta: false,
            message: 'Hihihi visitor',
            buttonMessage:'Click to see santa >:D'
        }
    }

    revealChioSanta() {
        this.setState((prevState) => ({
            showSanta: !prevState.showSanta,
            message: prevState.showSanta ? '(づ￣ ³￣)づ' : 'Hihihi visitor'
        }));
    }

    render() {
        if (this.showSanta) {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}> {buttonMessage} </button>
                    <img src={santa}/>

                </div>
            )        
        } else {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}>{buttonMessage}</button>
                </div>
            )
        }

    }
}

export default Message
