import React, {Component} from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state={
            userName: ''
        }
    }

    inputHandler = (event) => {
        this.setState({
            userName: event.target.value
        })
    }

    render() {
        return (
            <form>
                <label>Username</label>
                <input type='text' value={this.state.userName} onChange={this.inputHandler}></input>
            </form>
        )
    }
}

export default Form
