import React, {Component} from 'react'
import santa from './resources/hehehe.jpg'

class Message extends Component {
    constructor() {
        super()
        this.state = {
            showSanta: false,
            message: 'Hihihi visitor'
        }
    }

    revealChioSanta() {
        this.setState({
            showSanta: !this.showSanta,
            message: this.showSanta ? '(づ￣ ³￣)づ' : 'Hihihi visitor'
        });
        console.log("chio santa variable: " + this.showSanta)

    }

    render() {
        if (this.showSanta) {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}> :D </button>
                    <img src={santa}/>

                </div>
            )        
        } else {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}>Click to see santa :3</button>
                </div>
            )
        }

    }
}

export default Message
