import React, {Component} from 'react'
import santa from './resources/hehehe.jpg'

class Message extends Component {
    constructor() {
        super()
        this.state = {
            showSanta: false,
            message: 'Hihihi visitor'
        }
    }

    revealChioSanta() {
        this.setState(prevState => ({
            showSanta: !prevState.showSanta,
            message: showSanta ? '(づ￣ ³￣)づ' : 'Hihihi visitor'
        }));
    }

    render() {
        if (this.showSanta) {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}> :D </button>
                </div>
            )        
        } else {
            return (
                <div>
                    <h1>{this.state.message}</h1>
                    <button onClick={() => this.revealChioSanta()}>Click to see santa :3</button>
                    <img src={santa}/>
                </div>
            )
        }

    }
}

export default Message
